INSERT INTO `cmf_about_teacher_force_post` (`id`, `parent_id`, `post_type`, `post_format`, `user_id`, `post_status`, `create_time`, `update_time`, `published_time`, `delete_time`, `post_title`, `post_keywords`, `post_excerpt`, `post_content`, `post_content_filtered`, `more`) VALUES
(2, 0, 1, 1, 1, 1, 1505834245, 1505834245, 1505834225, 0, '师资力量', '', '', '\n&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 21px;font-family: 黑体;color: red&quot;&gt;学院荣誉&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt; &lt;/p&gt;\n&lt;p&gt;2015年10月荣获第十届全国高职高专“发明杯”大学生创新创业大赛优秀组织奖&lt;/p&gt;\n&lt;p&gt; &lt;/p&gt;\n&lt;p&gt;2014年荣获中国石油和化学工业联合会、中国化工教育协会石油和化工行业职业教育与培训全国示范性实训基地&lt;/p&gt;\n&lt;p&gt;2014年荣获克拉玛依市及独山子区“平安单位”荣誉称号&lt;/p&gt;\n&lt;p&gt;2014年荣获自治区第十三届运动会大会组委会授予“优秀志愿者集体”荣誉称号&lt;/p&gt;\n&lt;p&gt;2014年荣获新疆维吾尔自治区职业教育学会自治区职业院校优秀学生管理工作者&lt;/p&gt;\n&lt;p&gt; &lt;/p&gt;\n&lt;p&gt;2013年11月荣获自治区精神文明建设指导委员会“自治区精神文明单位”荣誉称号013荣获中华全国总工会授予“全国职工教育培训示范点”荣誉称号&lt;/p&gt;\n&lt;p&gt;2013年学院青年志愿者暑期社会实践活动获得了自治区“千队百优”团队称号&lt;/p&gt;\n&lt;p&gt;2013年初荣获市、油田公司级2010-2012年度维稳先进集体&lt;/p&gt;\n&lt;p&gt;2013年荣获克拉玛依市评为2013年度市级平安单位&lt;/p&gt;\n&lt;p&gt; &lt;/p&gt;\n&lt;p&gt;2012年荣获“新疆油田公司‘四好’领导班子”称号&lt;/p&gt;\n&lt;p&gt; &lt;/p&gt;\n&lt;p&gt;2011年荣获自治区教育厅招生工作先进单位荣誉称号&lt;/p&gt;\n&lt;p&gt;2011年荣获新疆油田公司2011年度安全生产与环境保护先进单位&lt;/p&gt;\n&lt;p&gt;2011年荣获“高教杯”全国大学生数学建模竞赛全国二等奖&lt;/p&gt;\n&lt;p&gt;2011年荣获新疆大学生、中学生田径运动会大学生乙组第三名，并获得“体育道德风尚奖”&lt;/p&gt;\n&lt;p&gt;2011年荣获克拉玛依市第一届创业大赛优秀奖&lt;/p&gt;\n&lt;p&gt;2010年获得克拉玛依市“平安校园”称号，并被评为独山子区综合治理先进单位&lt;/p&gt;\n&lt;p&gt; &lt;/p&gt;\n&lt;p&gt;2009年，由于学院维稳工作突出，受到了中共克拉玛依市委维稳领导小组的通令嘉勉和奖励，并颁发了《嘉勉令》&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2009&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;4&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;月荣获新疆维吾尔自治区“多威杯”大学生、中学生田径运动会“体育道德风尚奖”荣誉称号&lt;/span&gt; &lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2009&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;4&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;月荣获新疆维吾尔自治区“多威杯”大学生、中学生田径运动会团体总分第二名&lt;/span&gt;  &lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2008&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;10&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月荣获克拉玛依市创建无烟环境示范单位&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2008&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;8&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月克拉玛依职业技术学院荣获首届新疆高等职业院校职业规划设计征文比赛优秀组织奖&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2008&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;8&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月克拉玛依职业技术学院荣获自治区职业院校思想政治教育科研工作（&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2007---2008&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年度）先进学校&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2008&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;4&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月荣获克拉玛依市爱国卫生运动委员会颁发的&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2004--2007&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年巩固国家卫生城市“先进单位”&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;  &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2008&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;1&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月克拉玛依职业技术学院荣获克拉玛依市绿化委员会颁发的绿化先进单位&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;学院是唯一一家高职院校在自治区大学生运动会上获得三连冠的好成绩&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2007&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;10&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月获独山子区颁发的&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2007&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年度党报党刊发行工作先进集体称号&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2007&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;9&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月获&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt; “&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;第四届全国高职高专使用英语口语大赛&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;”&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;新疆赛区复赛&lt;/span&gt; &lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;优秀组织奖&lt;/span&gt;&lt;br&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2007&lt;span style=&quot;font-family: 宋体&quot;&gt;年&lt;/span&gt;8&lt;span style=&quot;font-family: 宋体&quot;&gt;月&lt;/span&gt;16&lt;span style=&quot;font-family: 宋体&quot;&gt;日&lt;/span&gt;&lt;span style=&quot;font-family: 宋体&quot;&gt;克拉玛依职业技术学院被评为自治区&lt;/span&gt;  “&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;国家示范性高等职业院校建设计划&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;”2007&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年立项申报院校&lt;/span&gt;&lt;br&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2007&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;7&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月获新疆教育厅、教育协会颁发的二&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;00&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;六&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;---&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;二&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;00&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;七年度后勤管理先进单位&lt;/span&gt;&lt;br&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2007&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;6&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体;color: #003366&quot;&gt;月获自治区&lt;/span&gt;&lt;span style=&quot;font-size: 12px;color: #003366&quot;&gt;2006--2007&lt;/span&gt;&lt;span style=&quo